import styled from "styled-components";
import { useHistory } from "react-router-dom";
import React, { useState } from "react";
import { useAppDispatch } from "../../store/hooks";


export default function SearchInput() {

    const [state, setState] = useState<string>();
    const [timeoutId, setTimeoutId] = useState<any>(-1);

    const dispatch = useAppDispatch();
    const history = useHistory();

    function onSubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault()
        push();
    }

    function push(searchText = state) {
        history.push('/customers?q=' + searchText);
    }

    return (
        <SearchContainer>
            <form onSubmit={onSubmit}>
                <input type='text' value={state} onChange={
                    async (e) => {
                        setState(e.target.value);
                        clearTimeout(timeoutId);
                        const _timeoutId: any = setTimeout(() => push(e.target.value), 500);
                        setTimeoutId(_timeoutId);

                    }}
                />
                <button type='submit' />
            </form>
        </SearchContainer>
    );
}

export const SearchContainer = styled(SkewBlock)`
    flex: 1 1 400px;

    &:before {
        background-color: ${colorTheme.secondary};

    }

    form {

        display: flex;
        height: 50px;
        z-index: 4;

        button {
            flex: 0 1 60px;
            border: none;
            background: transparent url(${assets.search_icon}) no-repeat center center;
            background-size: 65%;
        }

        input {
            flex: 1 1 40px;
            border: none;
            background-color: ${colorTheme.secondary.darken(0.15)};
            margin: 5px;
            border-radius: 20px;
            padding: 5px 18px 5px 18px;
            font-weight: 600;
            color: white;
            font-size: 17px;
            letter-spacing: 1px;
            outline: none;
        }
    }
`;
